css:
.text-start {
text-align: left;
}
html[dir="rtl"] .text-start {
text-align: right;
}
.text-end {
text-align: right;
}
html[dir="rtl"] .text-end {
text-align: left;
}
.table {
table-layout: auto;
margin-bottom: 0;
}
.table th, .table td {
vertical-align: middle;
white-space: nowrap;
text-align: center;
width: auto;
}
.table th.w-15, .table td.w-15 {
width: 15%;
}
.table th.fit, .table td.fit {
width: 1%;
}
.subheader {
font-size: 1.1rem;
}
